import React from 'react';

const About = () => (
  <div className="max-w-4xl mx-auto py-16 px-6 text-gray-200">
    <h1 className="text-4xl font-bold text-blue-500 mb-6">About Us</h1>
    <p className="mb-6">
      At HahuTech Solutions, we specialize in building data-centric digital ecosystems that drive decision-making and innovation.
      Our team brings expertise in AI, cloud technologies, and enterprise architecture to deliver scalable, secure, and intelligent solutions.
    </p>
    <p className="mb-6">
      With a passion for technology and a dedication to client success, we help organizations turn data into actionable insights—
      enabling agility, efficiency, and growth in today’s fast-paced digital economy.
    </p>
  </div>
);

export default About;
