// src/pages/Services.js
import React from 'react';

const services = [
  {
    title: "Data Engineering & Architecture",
    description: "Design and build scalable data pipelines, warehouses, and lakes to empower enterprise analytics and reporting.",
  },
  {
    title: "Cloud Strategy & Migration",
    description: "Develop cloud-native strategies and seamlessly migrate legacy systems to AWS, Azure, or GCP with minimal disruption.",
  },
  {
    title: "AI & Machine Learning Solutions",
    description: "Implement intelligent automation, predictive analytics, and natural language processing using custom AI models.",
  },
  {
    title: "Business Intelligence & Dashboards",
    description: "Create interactive dashboards and self-service analytics platforms using Power BI, Tableau, and Looker.",
  },
  {
    title: "System Integration & API Development",
    description: "Connect disparate systems through secure, scalable APIs and middleware to enhance data flow and operational efficiency.",
  },
];

const Services = () => (
  <div className="max-w-6xl mx-auto px-4 py-12 text-gray-200">
    <h2 className="text-3xl font-bold text-blue-400 mb-8">Our Services</h2>
    <div className="grid gap-8 sm:grid-cols-2">
      {services.map((service, index) => (
        <div key={index} className="bg-gray-800 rounded-lg shadow-lg p-6 hover:shadow-xl transition duration-300">
          <h3 className="text-xl font-semibold text-white mb-2">{service.title}</h3>
          <p className="text-gray-300">{service.description}</p>
        </div>
      ))}
    </div>
  </div>
);

export default Services;
